INSERT INTO `leavetypes` (`id`, `leave_type_name`, `description`, `work_age`, `holiday_days`,`createdAt`, `updatedAt`)
VALUES
	(1,'ลาหยุดพักผ่อนประจำปี อายุงาน 1 ปี','พนักงานที่ปฏิบัติงานกับบริษัทฯ ครบ 1 ปี สามารถลาหยุดพักผ่อนประจำปีได้โดยได้รับค่าจ้างเท่ากับวันทำงานปกติ โดยบริษัทฯ กำหนดจำนวนวันหยุดพักผ่อนประจำปีให้กับพนักงาน ดังนี้ คิดตามอายุงานโดยนับถึงเป็นปี พนักงานที่ประสงค์จะลาหยุดพักผ่อนประจำปีจะต้องขอและได้รับการอนุมัติจากผู้บังคับบัญชา ก่อนล่วงหน้าอย่างน้อย 5 วันทำงาน และบริษัทฯ อาจกำหนดหรือเปลี่ยนแปลงวันหยุดพักผ่อนประจำปีของพนักงานแต่ละคนได้ตามความเหมาะสม',1,7,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(2,'ลาหยุดพักผ่อนประจำปี อายุงาน 2 ปี','พนักงานที่ปฏิบัติงานกับบริษัทฯ ครบ 1 ปี สามารถลาหยุดพักผ่อนประจำปีได้โดยได้รับค่าจ้างเท่ากับวันทำงานปกติ โดยบริษัทฯ กำหนดจำนวนวันหยุดพักผ่อนประจำปีให้กับพนักงาน ดังนี้ คิดตามอายุงานโดยนับถึงเป็นปี พนักงานที่ประสงค์จะลาหยุดพักผ่อนประจำปีจะต้องขอและได้รับการอนุมัติจากผู้บังคับบัญชา ก่อนล่วงหน้าอย่างน้อย 5 วันทำงาน และบริษัทฯ อาจกำหนดหรือเปลี่ยนแปลงวันหยุดพักผ่อนประจำปีของพนักงานแต่ละคนได้ตามความเหมาะสม',2,8,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(3,'ลาหยุดพักผ่อนประจำปี อายุงานมากกว่า 5 ปี','พนักงานที่ปฏิบัติงานกับบริษัทฯ ครบ 1 ปี สามารถลาหยุดพักผ่อนประจำปีได้โดยได้รับค่าจ้างเท่ากับวันทำงานปกติ โดยบริษัทฯ กำหนดจำนวนวันหยุดพักผ่อนประจำปีให้กับพนักงาน ดังนี้ คิดตามอายุงานโดยนับถึงเป็นปี พนักงานที่ประสงค์จะลาหยุดพักผ่อนประจำปีจะต้องขอและได้รับการอนุมัติจากผู้บังคับบัญชา ก่อนล่วงหน้าอย่างน้อย 5 วันทำงาน และบริษัทฯ อาจกำหนดหรือเปลี่ยนแปลงวันหยุดพักผ่อนประจำปีของพนักงานแต่ละคนได้ตามความเหมาะสม',5,10,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(4,'ลากิจ','พนักงานมีสิทธิลางานเพื่อปฏิบัติกิจที่คนอื่นไม่สามารถทำการแทนได้และไม่สามารถทำกิจดังกล่าวในวันหยุดได้หรือมีความจำเป็นต้องไปประกอบพิธีศพของบิดา มารดา คู่สมรส บุตรของพนักงาน หรือการรับปริญญาตามหมายกำหนดการของมหาวิทยาลัย พนักงานมีสิทธิลากิจโดยได้รับค่าจ้างเท่ากับวันทำงานปกติ 6 วันต่อปี สำหรับพนักงานที่ได้รับการบรรจุเป็นพนักงานแล้วเท่านั้น พนักงานที่ประสงค์จะลาหยุดจะต้องขอและได้รับการอนุมัติจากผู้บังคับบัญชาโดยตรง ก่อนล่วงหน้า 3 วันทำงาน การลากิจตามความจำเป็นที่ต้องลา 1-2 ชั่วโมง ให้บันทึกการลาในระบบและแจ้งหัวหน้างานตามปกติ จำนวนชั่วโมงที่ลาจะนำมาสะสม ',0,6,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(5,'ลาป่วย','พนักงานมีสิทธิในการลาป่วยได้เท่าที่ป่วยจริงโดยได้รับค่าจ้างเท่ากับวันทำงานปกติไม่เกินปีละ 30 วันส่วนที่เกิน 30 วันทำงานจะไม่ได้รับค่าจ้าง พนักงานที่ต้องการลาเนื่องจากป่วยต้องแจ้งให้ผู้บังคับบัญชาโดยตรงรับทราบโดยเร็วที่สุดภายใน เวลา 09.00 น. โดยการโทรแจ้งและจะต้องบันทึกลาในระบบ และสำหรับพนักงานที่อยู่ในช่วงทดลองงานจะต้องแนบเอกสารใบรับรองแพทย์ในระบบให้ผู้บังคับบัญชาอนุมัติในวันแรกของการกลับมาทำงานด้วยทุกครั้ง',0,30,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(6,'ลาคลอด','พนักงานหญิงที่มีครรภ์มีสิทธิลาเพื่อคลอดบุตรได้ก่อนและหลังคลอดบุตรครรภ์หนึ่งรวมกันไม่เกิน 90 วันโดยนับรวมวันหยุดที่มีระหว่างวันลาด้วยทั้งนี้บริษัทฯจะจ่ายค่าจ้างเท่ากับค่าจ้างในวันทำงานปกติตลอดระยะเวลาที่ลาแต่ไม่เกิน 45 วันและอีก 45 วันติดต่อรับที่ประกันสังคมแต่พนักงานจะต้องจ่ายประกันสังคมครบ 7 เดือนจึงจะสามารถเบิกได้ เมื่อกลับมาทำงานให้ส่งสำเนาสูติบัตรของบุตรให้ฝ่ายบุคคลเพื่อเปลี่ยนแปลงสถานะภาพการลดหย่อนภาษีเงินได้ของพนักงาน การแท้งบุตรก่อนตั้งครรภ์ครบ 28 สัปดาห์ ไม่ถือเป็นการคลอดบุตร ให้ถือเป็นการลาป่วย การลาเพื่อพบแพทย์ระหว่างการตั้งครรภ์ถือเป็นการลากิจ',0,90,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(7,'การลาเพื่อรับราชการทหาร','พนักงานชายลาเพื่อรับราชการทหารในการเรียกพลเพื่อตรวจสอบเพื่อฝึกวิชาทหารหรือเพื่อทดลองความพรั่งพร้อมตามกฎหมายว่าด้วยการรับราชการทหารมีสิทธิได้รับค่าจ้างเท่ากับวันทำงานตลอดระยะเวลาที่ลาไม่เกินปีละ 60 วันโดยบันทึกลาในระบบพร้อมแนบหนังสือเรียกตัวจากทางราชการฉบับจริง เมื่อครบกำหนดระยะเวลาการลาดังกล่าว ให้พนักงานรายงานตัวเข้าปฏิบัติงานในวันทำงานถัดไป โดยให้แสดงหลักฐานการลาต่อผู้บังคับบัญชา ภายใน 7 วัน นับแต่วันที่รายงานตัว มิฉะนั้นจะถือว่าขาดงาน การลารับราชการตามข้อนี้มิได้หมายความถึงการลาไปเกณฑ์ทหาร หากพนักงานต้องไปเป็นทหารตามหมายเกณฑ์ พนักงานจะต้องลาออกจากการเป็นพนักงานของบริษัทฯ ทันที',0,0,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(8,'การลาอุปสมบท','พนักงานประจำชาย อายุงานตั้งแต่ 1 ปีขึ้นไปไม่เคยอุปสมบทมาก่อนมีสิทธิลาเพื่ออุปสมบทได้ 1 ครั้ง ตลอดอายุการทำงานกับบริษัทฯ พนักงานสามารถลาอุปสมบทได้ 15 วัน นับรวมวันหยุด บริษัทฯจะจ่ายค่าจ้างเท่ากับค่าจ้างในวันทำงานปกติตลอดระยะเวลาที่ลา พนักงานชายที่ประสงค์จะใช้สิทธิลาเพื่ออุปสมบท จะต้องขออนุมัติลาล่วงหน้าและได้รับการอนุมัติจากผู้บังคับบัญชาล่วงหน้าอย่างน้อย 30 วัน โดยกำหนดวันลาและวันกลับมาทำงานอย่างชัดเจนพร้อมทั้งแสดงหนังสือรับรองการอุปสมบท บริษัทฯสงวนสิทธิที่จะไม่อนุมัติให้ลาหรือขอให้เลื่อนหรือลดจำนวนวันลาลงได้หากเห็นว่าอาจมีความเสียหายต่องาน',0,15,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(9,'ลาเพื่อทำหมัน','พนักงานมีสิทธิลาเพื่อทำหมันได้ตามระยะเวลาที่แพทย์แผนปัจจุบันชั้นหนึ่งกำหนดและออกใบรับรองให้ โดยบริษัทฯ จะจ่ายค่าจ้างให้เท่ากับค่าจ้างในวันทำงานปกติตามจำนวนวันที่พนักงานลาเพื่อทำหมันเท่านั้น พนักงานจะต้องยื่นหลักฐานประกอบการลาในวันแรกที่กลับเข้ามาทำงาน',0,0,'2018-03-20 03:47:40','2018-03-20 03:47:40'),
	(10,'ลาเพื่อการฝึกอบรมหรือพัฒนาความรู้','พนักงานมีสิทธิลาเพื่อการฝึกอบรมหรือพัฒนาความรู้ความสามารถ ตามหลักเกณฑ์และวิธีการที่กำหนดในกฎกระทรวง ฉบับที่ 5 พ.ศ. 2541 ออกตามความใน พ.ร.บ. คุ้มครองแรงงาน พ.ศ. 2541 ดังนี้ เพื่อประโยชน์ต่อการแรงงานและสวัสดิการสังคมหรือเพิ่มทักษะ ความชำนาญ เพื่อเพิ่มประสิทธิภาพในการทำงานของพนักงาน การสอบวัดผลทางการศึกษาที่ทางราชการจัด หรืออนุญาตให้จัดขึ้น ทั้งนี้ การฝึกอบรม หรือพัฒนาความรู้จะต้องมีโครงการหรือหลักสูตรและกำหนดช่วงเวลาที่แน่นอนและชัดเจน พนักงานสามารถลาเพื่อการฝึกอบรมหรือพัฒนาความรู้ได้ไม่เกินปีละ 3 ครั้ง และรวมวันลาแล้วจะต้องไม่เกิน 15 วันต่อรอบปีปฏิทินโดยได้รับค่าจ้างในวันทำงานปกติ พนักงานที่ประสงค์จะลาเพื่อการฝึกอบรมหรือพัฒนาความรู้จะต้องยื่นใบลาให้ผู้บังคับบัญชาอนุมัติล่วงหน้าไม่น้อยกว่า 7 วัน บริษัทฯ อาจไม่อนุญาตให้พนักงานลาเพื่อการฝึกอบรมหรือพัฒนาความรู้หากการลานั้นก่อให้เกิดความเสียหาย หรือกระทบกระเทือนต่อธุรกิจของบริษัทฯ',0,45,'2018-03-20 03:47:40','2018-03-20 03:47:40');